import java.util.Scanner;public class PV_02_Palindrome{static public void main(String args[]){Scanner sc = new Scanner(System.in);System.out.println("Enter the number to check palindrome or not:");int no = sc.nextInt();int temp = no, val = 0;while(no!=0){int rem = no%10;no = no/10;val = val * 10 + rem;}if(temp == val)System.out.println("No is palindrome."); else System.out.println("No is not palindrome");}}
